import React, { useState } from 'react';
import '../css/login.css';

function Signup() {
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');
  const [agreement, setAgreement] = useState(false);
  const [error, setError] = useState('');
  const [isIdChecked, setIsIdChecked] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!name || !phone || !userId || !password) {
      setError('모든 항목을 입력해주세요.');
      return;
    }

    if (!isIdChecked) {
      setError('아이디 중복 확인을 해주세요.');
      return;
    }

    if (!agreement) {
      setError('약관에 동의해주세요.');
      return;
    }

    setError('');
    alert(`회원가입 완료! 🎉\n이름: ${name}, 아이디: ${userId}`);
  };

  const handleCheckId = () => {
    if (!userId.trim()) {
      alert('아이디를 입력해주세요.');
      return;
    }
    // 여기서 실제 중복 확인 API 호출 가능
    alert(`아이디 "${userId}"는 사용 가능합니다.`);
    setIsIdChecked(true);
  };

  return (
    <div className="login-container">
      <h1 className="spotlight-logo">
        <span className="black">SPOT</span>
        <span className="light animated">LIGHT</span>
      </h1>

      <form className="signup-box" onSubmit={handleSubmit}>
        <h2>가입을 시작합니다!</h2>

        <div className="form-section">
          <h3 className="form-title">회원 정보</h3>
          <div className="input-wrap">
            <input
              type="text"
              placeholder="이름"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="input-wrap">
            <input
              type="tel"
              placeholder="휴대폰 번호"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
            />
          </div>
        </div>

        <div className="form-section">
          <h3 className="form-title">계정</h3>
          <div className="input-wrap id-check">
            <input
              type="text"
              placeholder="아이디 입력"
              value={userId}
              onChange={(e) => {
                setUserId(e.target.value);
                setIsIdChecked(false); // 변경 시 다시 확인 필요
              }}
            />
            <button type="button" className="check-btn" onClick={handleCheckId}>
              중복 확인
            </button>
          </div>

          <div className="input-wrap">
            <input
              type="password"
              placeholder="비밀번호 입력"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
        </div>

        <div className="terms">
          <input
            type="checkbox"
            id="agree"
            checked={agreement}
            onChange={(e) => setAgreement(e.target.checked)}
          />
          <label htmlFor="agree">이용 약관에 동의합니다.</label>
        </div>

        {error && <div className="error-message">{error}</div>}

        <button type="submit" className="btn login-btn">
          회원가입 완료
        </button>
      </form>
    </div>
  );
}

export default Signup;
