// src/pages/Signup.js
import React, { useState } from 'react';
import '../css/login.css';

function Signup() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [pw, setPw] = useState('');

  const handleSignup = () => {
    if (!name.trim() || !email.trim() || !pw.trim()) {
      alert('모든 항목을 입력해주세요.');
      return;
    }

    alert('회원가입 완료!');
  };

  return (
    <div className="login-container">
      <h1 className="spotlight-logo">
        <span className="black">SPOT</span>
        <span className="light animated">LIGHT</span>
      </h1>

      <div className="signup-box">
        <h2>가입을 시작합니다!</h2>

        <div className="input-wrap">
          <input
            type="text"
            placeholder="이름"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>

        <div className="input-wrap">
          <input
            type="email"
            placeholder="이메일"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>

        <div className="input-wrap password-line">
          <input
            type="password"
            placeholder="비밀번호"
            value={pw}
            onChange={(e) => setPw(e.target.value)}
          />
        </div>

        <button className="btn login-btn" onClick={handleSignup}>
          SPOTLIGHT 회원가입하기
        </button>
      </div>
    </div>
  );
}

export default Signup;
