import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../css/myPage/myPage_header.css';

function MainPageHeader() {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showDropdown, setShowDropdown] = useState(false);
  const dropdownRef = useRef(null);

  useEffect(() => {
    const loginStatus = localStorage.getItem('loggedIn');
    setIsLoggedIn(loginStatus === 'true');
  }, []);

  useEffect(() => {
    function handleClickOutside(e) {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
        setShowDropdown(false);
      }
    }

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('loggedIn');
    setIsLoggedIn(false);
    setShowDropdown(false);
    navigate('/');
  };

  const handleMyPage = () => {
    setShowDropdown(false);
    navigate('/mypage');
  };

  const handleLogin = () => {
    navigate('/login');
  };

  return (
    <div className="mypage-wrapper">
      <div className="mypage-header">
        <div
          className="logo"
          onClick={() => navigate('/mainpage')}
          style={{ cursor: 'pointer' }}
        >
          <span className="black">SPOT</span>
          <span className="light animated">LIGHT</span>
        </div>

        <div className="search-box">
          <span className="search-icon">üîç</span>
          <input type="text" placeholder="Ï∞æÍ≥† Ïã∂ÏùÄ ÏÉÅÌíàÏùÑ ÏûÖÎ†•Ìï¥Î≥¥ÏÑ∏Ïöî." />
        </div>

        <div className="nav-links">
          {isLoggedIn ? (
            <>
              <div className="nav-item">üí¨ Ï±ÑÌåÖÌïòÍ∏∞</div>
              <div className="divider" />
              <div className="nav-item">üì¶ ÎåÄÏó¨ÌïòÍ∏∞</div>
              <div className="divider" />
              <div className="nav-item user-menu" ref={dropdownRef}>
                <div
                  onClick={() => setShowDropdown(!showDropdown)}
                  style={{ cursor: 'pointer' }}
                >
                  üë§ ÎßàÏù¥ ÌéòÏù¥ÏßÄ
                </div>
                {showDropdown && (
                  <div className="dropdown">
                    <div onClick={handleMyPage} className="dropdown-item">
                      ÎßàÏù¥ÌéòÏù¥ÏßÄ
                    </div>
                    <div onClick={handleLogout} className="dropdown-item">
                      Î°úÍ∑∏ÏïÑÏõÉ
