import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './chat.css';

const dummyRooms = [
  {
    id: 1,
    name: 'Ï∞¢Ïñ¥ÏßÑ Ï†ïÏû•',
    owner: 'ÍπÄÏ≤†Ïàò',
    image: '/product.png',
    status: 'ÎåÄÏó¨Ï§ë',
    scheduleDate: '',
    schedulePlace: '',
    messages: [
      {
        id: 1,
        user: 'ÏÉÅÎåÄ',
        text: 'ÎÑ§, ÌåêÎß§ Ï§ëÏûÖÎãàÎã§.',
        time: '2025-05-02T13:24:00',
        isMe: false
      },
      {
        id: 2,
        user: 'ÎÇò',
        text: 'ÎÇ¥Ïùº ÏßÅÍ±∞Îûò Í∞ÄÎä•Ìï†ÍπåÏöî?',
        time: '2025-05-03T13:25:00',
        isMe: true
      }
    ]
  }
];

// Ï†ÑÏ≤¥ ChatPage Ïª¥Ìè¨ÎÑåÌä∏ ÏΩîÎìú
function ChatPage() {
  const navigate = useNavigate();
  const [rooms, setRooms] = useState(dummyRooms);
  const [currentRoomId, setCurrentRoomId] = useState(1);
  const [messages, setMessages] = useState(dummyRooms[0].messages);
  const [input, setInput] = useState('');
  const [files, setFiles] = useState([]);
  const [showScheduleForm, setShowScheduleForm] = useState(false);
  const [scheduleDate, setScheduleDate] = useState('');
  const [schedulePlace, setSchedulePlace] = useState('');
  const fileRef = useRef();
  const scrollRef = useRef();

  const currentRoom = rooms.find(r => r.id === currentRoomId);

  useEffect(() => {
    const updatedRoom = rooms.find(r => r.id === currentRoomId);
    if (updatedRoom) setMessages(updatedRoom.messages);
  }, [currentRoomId, rooms]);

  useEffect(() => {
    scrollRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const formatDateLabel = (isoString) => {
    const date = new Date(isoString);
    return `${date.getFullYear()}ÎÖÑ ${String(date.getMonth() + 1).padStart(2, '0')}Ïõî ${String(date.getDate()).padStart(2, '0')}Ïùº`;
  };

  const shouldShowDate = (current, previous) => {
    const currDate = new Date(current).toDateString();
    const prevDate = previous ? new Date(previous).toDateString() : null;
    return currDate !== prevDate;
  };

  const handleSend = () => {
    if (!input.trim() && files.length === 0) return;

    const now = new Date();
    const newMessages = [];

    if (input.trim()) {
      newMessages.push({
        id: messages.length + newMessages.length + 1,
        user: 'ÎÇò',
        text: input,
        image: null,
        time: now.toISOString(),
        isMe: true
      });
    }

    if (files.length) {
      newMessages.push(
        ...files.map((f, i) => ({
          id: messages.length + newMessages.length + i + 1,
          user: 'ÎÇò',
          text: null,
          image: URL.createObjectURL(f),
          time: now.toISOString(),
          isMe: true
        }))
      );
    }

    const updatedRooms = rooms.map(room =>
      room.id === currentRoomId
        ? { ...room, messages: [...room.messages, ...newMessages] }
        : room
    );

    setRooms(updatedRooms);
    setMessages(prev => [...prev, ...newMessages]);
    setInput('');
    setFiles([]);
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') handleSend();
  };

  const handleStatusChange = (e) => {
    const newStatus = e.target.value;
    const now = new Date();
    const statusMessage = {
      id: messages.length + 1,
      user: 'ÏãúÏä§ÌÖú',
      text: `"${currentRoom.name}"Ïùò ÏÉÅÌÉúÍ∞Ä "${newStatus}"(Ïúº)Î°ú Î≥ÄÍ≤ΩÎêòÏóàÏäµÎãàÎã§.`,
      time: now.toISOString(),
      isMe: false
    };

    const updatedRooms = rooms.map(room => {
      if (room.id === currentRoomId) {
        const updatedMessages = [...room.messages, statusMessage];
        return { ...room, status: newStatus, messages: updatedMessages };
      }
      return room;
    });

    setRooms(updatedRooms);
    const updatedCurrentRoom = updatedRooms.find(r => r.id === currentRoomId);
    setMessages(updatedCurrentRoom.messages);
  };

  const handleScheduleSubmit = () => {
    if (!scheduleDate || !schedulePlace) {
      alert('ÎÇ†ÏßúÏôÄ Ïû•ÏÜåÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî.');
      return;
    }

    const now = new Date();
    const encodedPlace = encodeURIComponent(schedulePlace);
    const mapUrl = `https://www.google.com/maps?q=${encodedPlace}&output=embed`;
    const formattedDateTime = scheduleDate.replace('T', ' ').slice(0, 16);

    const scheduleMessage = {
      id: messages.length + 1,
      user: 'ÎÇò',
      text: null,
      map: mapUrl,
      scheduleDateTime: formattedDateTime,
      schedulePlace: schedulePlace,
      time: now.toISOString(),
      isMe: true
    };

    const systemMessage = {
      id: messages.length + 2,
      user: 'ÏãúÏä§ÌÖú',
      text: `"${currentRoom.name}"Ïùò ÏÉÅÌÉúÍ∞Ä "ÏòàÏïΩÏ§ë"ÏúºÎ°ú ÏûêÎèô Î≥ÄÍ≤ΩÎêòÏóàÏäµÎãàÎã§.`,
      time: now.toISOString(),
      isMe: false
    };

    const updatedRooms = rooms.map(room =>
      room.id === currentRoomId
        ? {
            ...room,
            messages: [...room.messages, scheduleMessage, systemMessage],
            scheduleDate,
            schedulePlace,
            status: 'ÏòàÏïΩÏ§ë'
          }
        : room
    );

    setRooms(updatedRooms);
    const updatedCurrentRoom = updatedRooms.find(r => r.id === currentRoomId);
    setMessages(updatedCurrentRoom.messages);
    setShowScheduleForm(false);
    setScheduleDate('');
    setSchedulePlace('');
  };

  const handleScheduleDelete = () => {
    const now = new Date();
    const systemMessage1 = {
      id: messages.length + 1,
      user: 'ÏãúÏä§ÌÖú',
      text: 'ÏùºÏ†ïÏù¥ ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.',
      time: now.toISOString(),
      isMe: false
    };

    const updatedRooms = rooms.map(room => {
      if (room.id === currentRoomId) {
        const filteredMessages = room.messages.filter(msg => !msg.map);
        return {
          ...room,
          scheduleDate: '',
          schedulePlace: '',
          status: 'ÎåÄÏó¨Ï§ë',
          messages: [
            ...filteredMessages,
            systemMessage1,
            {
              id: filteredMessages.length + 2,
              user: 'ÏãúÏä§ÌÖú',
              text: `"${room.name}"Ïùò ÏÉÅÌÉúÍ∞Ä "ÎåÄÏó¨Ï§ë"ÏúºÎ°ú ÏûêÎèô Î≥ÄÍ≤ΩÎêòÏóàÏäµÎãàÎã§.`,
              time: now.toISOString(),
              isMe: false
            }
          ]
        };
      }
      return room;
    });

    setRooms(updatedRooms);
    const updatedCurrentRoom = updatedRooms.find(r => r.id === currentRoomId);
    setMessages(updatedCurrentRoom.messages);
  };

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      <div style={{ width: '50%', borderRight: '1px solid #ccc', padding: '10px' }}>
        <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
          <div className="main-logo" onClick={() => navigate('/main')} style={{ cursor: 'pointer' }}>
            <span className="black">SPOT</span>
            <span className="light animated">LIGHT</span>
          </div>
        </div>
        {rooms.map(room => (
          <div key={room.id} onClick={() => setCurrentRoomId(room.id)}
            style={{ padding: '10px', backgroundColor: currentRoomId === room.id ? '#fef08a' : '', cursor: 'pointer' }}>
            <strong>{room.owner}</strong><br />
            {room.name}<br />
            ÏÉÅÌÉú: {room.status}
          </div>
        ))}
      </div>

      <div style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
        <div style={{
          padding: '10px',
          borderBottom: '1px solid #ccc',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center'
        }}>
          <div style={{ display: 'flex', alignItems: 'center' }}>
            <img src={currentRoom.image} alt="ÏÉÅÌíà" style={{ width: '40px', height: '40px', borderRadius: '8px', marginRight: '10px' }} />
            <div>
              <div style={{ fontWeight: 'bold', fontSize: '14px', color: '#555' }}>{currentRoom.owner}</div>
              <div style={{ fontSize: '18px', fontWeight: 'bold' }}>{currentRoom.name}</div>
              {currentRoom.scheduleDate && currentRoom.schedulePlace && (
                <div style={{ fontSize: '14px', color: '#666', marginTop: '4px' }}>
                  üìÖ {currentRoom.scheduleDate.replace('T', ' ').slice(0, 16)} ¬∑ üìç {currentRoom.schedulePlace}
                  <button
                    onClick={handleScheduleDelete}
                    style={{ marginLeft: '10px', fontSize: '12px', color: 'red', background: 'none', border: 'none', cursor: 'pointer' }}
                  >
                    ‚ùå ÏÇ≠Ï†ú
                  </button>
                </div>
              )}
            </div>
          </div>
          <div style={{ display: 'flex', gap: '8px' }}>
            {['ÎåÄÏó¨Ï§ë', 'ÏòàÏïΩÏ§ë', 'ÎåÄÏó¨ÏôÑÎ£å'].map(status => (
              <button
                key={status}
                onClick={() => handleStatusChange({ target: { value: status } })}
                style={{
                  padding: '6px 12px',
                  fontSize: '14px',
                  border: currentRoom.status === status ? '2px solid #555' : '1px solid #ccc',
                  backgroundColor: currentRoom.status === status ? '#fef08a' : '#fff',
                  borderRadius: '6px',
                  cursor: 'pointer'
                }}
              >
                {status}
              </button>
            ))}
          </div>
        </div>

        <div style={{ flex: 1, overflowY: 'auto', padding: '10px' }}>
          {messages.map((msg, idx) => (
            <div key={msg.id}>
              {shouldShowDate(msg.time, idx > 0 ? messages[idx - 1].time : null) && (
                <div style={{ textAlign: 'center', margin: '10px 0', fontSize: '12px', color: '#888' }}>
                  {formatDateLabel(msg.time)}
                </div>
              )}
              <div style={{ textAlign: msg.isMe ? 'right' : 'left', margin: '4px 0' }}>
                {msg.text && (
                  <div style={{
                    display: 'inline-block',
                    background: msg.isMe ? '#fef08a' : '#eee',
                    padding: '8px 12px',
                    borderRadius: '12px',
                    maxWidth: '60%',
                    whiteSpace: 'pre-wrap'
                  }}>
                    {msg.text}
                  </div>
                )}
                {msg.image && (
                  <div style={{ marginTop: '8px' }}>
                    <img src={msg.image} alt="Ï≤®Î∂Ä Ïù¥ÎØ∏ÏßÄ" style={{ maxWidth: '200px', borderRadius: '8px' }} />
                  </div>
                )}
                {msg.map && (
                  <div style={{ marginTop: '8px' }}>
                    <iframe
                      title="ÏßÄÎèÑ"
                      src={msg.map}
                      width="300"
                      height="150"
                      style={{ borderRadius: '8px' }}
                      allowFullScreen=""
                      loading="lazy"
                    />
                    {msg.scheduleDateTime && msg.schedulePlace && (
                      <div style={{ fontSize: '13px', color: '#555', marginTop: '4px' }}>
                        üìÖ {msg.scheduleDateTime} ¬∑ üìç {msg.schedulePlace}
                      </div>
                    )}
                  </div>
                )}
              </div>
            </div>
          ))}
          <div ref={scrollRef} />
        </div>

        {showScheduleForm && (
          <div style={{ padding: '10px', backgroundColor: '#f9f9f9', borderTop: '1px solid #ddd' }}>
            <input type="datetime-local" value={scheduleDate} onChange={(e) => setScheduleDate(e.target.value)} style={{ marginRight: '10px' }} />
            <input type="text" placeholder="Ïû•ÏÜå ÏûÖÎ†•" value={schedulePlace} onChange={(e) => setSchedulePlace(e.target.value)} style={{ marginRight: '10px' }} />
            <button onClick={handleScheduleSubmit}>Îì±Î°ù</button>
          </div>
        )}

        <div style={{
          display: 'flex',
          alignItems: 'center',
          padding: '10px',
          borderTop: '1px solid #ccc',
          gap: '8px',
          flexShrink: 0
        }}>
          <button onClick={() => setShowScheduleForm(prev => !prev)}>üìÖ</button>
          <div style={{ position: 'relative', flexGrow: 1 }}>
            <input
              type="text"
              placeholder="Ïó∞ÎùΩÏùÑ ÌÜµÌï¥ ÎåÄÏó¨Î•º ÏãúÏûëÌï¥Î≥¥ÏÑ∏Ïöî."
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={handleKeyDown}
              style={{
                width: '100%',
                padding: '8px 40px 8px 12px',
                borderRadius: '8px',
                border: '1px solid #ccc',
                boxSizing: 'border-box'
              }}
            />
            <button
              onClick={() => fileRef.current.click()}
              style={{
                position: 'absolute',
                right: '8px',
                top: '40%',
                transform: 'translateY(-50%)',
                background: 'none',
                border: 'none',
                cursor: 'pointer',
                fontSize: '18px'
              }}
            >
              üì∑
            </button>
            <input
              type="file"
              ref={fileRef}
              style={{ display: 'none' }}
              multiple
              onChange={(e) => {
                const selectedFiles = Array.from(e.target.files);
                if (selectedFiles.length) setFiles(selectedFiles);
                fileRef.current.value = null;
              }}
            />
          </div>
          <button onClick={handleSend} style={{ padding: '8px 12px' }}>Ï†ÑÏÜ°</button>
        </div>
      </div>
    </div>
  );
}

export default ChatPage;
