import React, { useState } from 'react';
import '../css/login.css';

function Login() {
  const [id, setId] = useState('');
  const [pw, setPw] = useState('');
  const [error, setError] = useState('');
  const [showPw, setShowPw] = useState(false);
  const [showTooltip, setShowTooltip] = useState(false); // ⓘ 토글용

  const handleLogin = () => {
    if (!id.trim() || !pw.trim()) {
      setError('아이디와 비밀번호를 모두 입력해주세요.');
      return;
    }

    setError('');
    alert('로그인 성공!');
  };

  return (
    <div className="login-container">
      <h1 className="spotlight-logo">
        <span className="black">SPOT</span>
        <span className="light animated">LIGHT</span>
      </h1>

      <div className="login-box">
        <input
          type="text"
          placeholder="SPOTLIGHT 아이디"
          value={id}
          onChange={(e) => setId(e.target.value)}
        />

        <div className="password-wrap">
          <input
            type={showPw ? 'text' : 'password'}
            placeholder="비밀번호"
            value={pw}
            onChange={(e) => setPw(e.target.value)}
          />
          <button
            type="button"
            className="toggle-btn"
            onClick={() => setShowPw(!showPw)}
          >
            {showPw ? '숨기기' : '보기'}
          </button>
        </div>

        {error && <div className="error">{error}</div>}

        <div className="remember">
          <input type="checkbox" id="remember" />
          <label htmlFor="remember">간편로그인 정보 저장</label>

          <div className="tooltip-wrapper">
            <button
              type="button"
              className="tooltip-icon"
              onClick={() => setShowTooltip(!showTooltip)}
            >
              ⓘ
            </button>
            {showTooltip && (
              <div className="tooltip-box">
                로그인한 계정의 정보가 저장됩니다. 개인정보 보호를 위해<br />
                개인 기기에서만 사용해 주세요.<br />
                <a href="#" className="tooltip-link">도움말 보기</a>
              </div>
            )}
          </div>
        </div>

        <button className="btn login-btn" onClick={handleLogin}>
          로그인
        </button>

        <div className="links">
          <a href="/signup">회원가입</a>
          <span> | </span>
          <a href="/find-account">계정 찾기</a>
          <span> | </span>
          <a href="/find-password">비밀번호 찾기</a>
        </div>
      </div>
    </div>
  );
}

export default Login;
