// src/pages/reviewPage/reviewPage.js
import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import MyPageHeader from '../myPage/myPage_header';
import MyPageCategory from '../myPage/myPage_category';
import ReviewPageReview from './reviewPage_review';

import '../../css/reviewPage/reviewPage.css'; // CSS 연결

function ReviewPage() {
  return (
    <div>
      <MyPageHeader />
      <MyPageCategory />

      {/* ✅ 페이지 전체를 감싸는 flex 래퍼 */}
      <div className="review-layout">
        <MyPageMenu /> {/* 좌측 메뉴 */}

        {/* 우측 본문 라우팅 영역 */}
        <div className="review-body">
          <Routes>
            <Route path="/" element={<Navigate to="review" replace />} />
            <Route path="review" element={<ReviewPageReview />} />
          </Routes>
        </div>
      </div>
    </div>
  );
}

export default ReviewPage;
