import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../css/chatPage/chatPage.css';

const dummyRooms = [
  {
    id: 1,
    name: 'Ï∞¢Ïñ¥ÏßÑ Ï†ïÏû•',
    owner: 'ÍπÄÏ≤†Ïàò',
    image: '/product.png',
    status: 'ÎåÄÏó¨Ï§ë',
    scheduleDate: '',
    schedulePlace: '',
    messages: [
      {
        id: 1,
        user: 'ÏÉÅÎåÄ',
        text: 'ÎÑ§, ÌåêÎß§ Ï§ëÏûÖÎãàÎã§.',
        time: '2025-05-02T13:24:00',
        isMe: false
      },
      {
        id: 2,
        user: 'ÎÇò',
        text: 'ÎÇ¥Ïùº ÏßÅÍ±∞Îûò Í∞ÄÎä•Ìï†ÍπåÏöî?',
        time: '2025-05-03T13:25:00',
        isMe: true
      }
    ]
  }
];

function ChatPage() {
  const navigate = useNavigate();
  const [rooms, setRooms] = useState(dummyRooms);
  const [currentRoomId, setCurrentRoomId] = useState(1);
  const [messages, setMessages] = useState(dummyRooms[0].messages);
  const [input, setInput] = useState('');
  const [files, setFiles] = useState([]);
  const [showScheduleForm, setShowScheduleForm] = useState(false);
  const [scheduleDate, setScheduleDate] = useState('');
  const [schedulePlace, setSchedulePlace] = useState('');
  const fileRef = useRef();
  const scrollRef = useRef();

  const currentRoom = rooms.find(r => r.id === currentRoomId);

  useEffect(() => {
    const updatedRoom = rooms.find(r => r.id === currentRoomId);
    if (updatedRoom) setMessages(updatedRoom.messages);
  }, [currentRoomId, rooms]);

  useEffect(() => {
    scrollRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const formatDateLabel = (isoString) => {
    const date = new Date(isoString);
    return `${date.getFullYear()}ÎÖÑ ${String(date.getMonth() + 1).padStart(2, '0')}Ïõî ${String(date.getDate()).padStart(2, '0')}Ïùº`;
  };

  const formatTime = (isoString) => {
    const date = new Date(isoString);
    const hours = date.getHours();
    const minutes = String(date.getMinutes()).padStart(2, '0');
    const isAM = hours < 12;
    const formattedHour = hours % 12 || 12;
    return `${isAM ? 'Ïò§Ï†Ñ' : 'Ïò§ÌõÑ'} ${formattedHour}:${minutes}`;
  };

  const shouldShowDate = (current, previous) => {
    const currDate = new Date(current).toDateString();
    const prevDate = previous ? new Date(previous).toDateString() : null;
    return currDate !== prevDate;
  };

  const handleSend = () => {
    if (!input.trim() && files.length === 0) return;

    const now = new Date();
    const newMessages = [];

    if (input.trim()) {
      newMessages.push({
        id: messages.length + newMessages.length + 1,
        user: 'ÎÇò',
        text: input,
        image: null,
        time: now.toISOString(),
        isMe: true
      });
    }

    if (files.length) {
      newMessages.push(
        ...files.map((f, i) => ({
          id: messages.length + newMessages.length + i + 1,
          user: 'ÎÇò',
          text: null,
          image: URL.createObjectURL(f),
          time: now.toISOString(),
          isMe: true
        }))
      );
    }

    const updatedRooms = rooms.map(room =>
      room.id === currentRoomId
        ? { ...room, messages: [...room.messages, ...newMessages] }
        : room
    );

    setRooms(updatedRooms);
    setMessages(prev => [...prev, ...newMessages]);
    setInput('');
    setFiles([]);
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') handleSend();
  };

  const handleStatusChange = (e) => {
    const newStatus = e.target.value;
    const now = new Date();
    const statusMessage = {
      id: messages.length + 1,
      user: 'ÏãúÏä§ÌÖú',
      text: `"${currentRoom.name}"Ïùò ÏÉÅÌÉúÍ∞Ä "${newStatus}"(Ïúº)Î°ú Î≥ÄÍ≤ΩÎêòÏóàÏäµÎãàÎã§.`,
      time: now.toISOString(),
      isMe: false
    };

    const updatedRooms = rooms.map(room =>
      room.id === currentRoomId
        ? { ...room, status: newStatus, messages: [...room.messages, statusMessage] }
        : room
    );

    setRooms(updatedRooms);
    setMessages(updatedRooms.find(r => r.id === currentRoomId).messages);
  };

  const handleScheduleSubmit = () => {
    if (!scheduleDate || !schedulePlace) {
      alert('ÎÇ†ÏßúÏôÄ Ïû•ÏÜåÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî.');
      return;
    }

    const now = new Date();
    const encodedPlace = encodeURIComponent(schedulePlace);
    const mapUrl = `https://www.google.com/maps?q=${encodedPlace}&output=embed`;
    const formattedDateTime = scheduleDate.replace('T', ' ').slice(0, 16);

    const scheduleMessage = {
      id: messages.length + 1,
      user: 'ÎÇò',
      map: mapUrl,
      scheduleDateTime: formattedDateTime,
      schedulePlace: schedulePlace,
      time: now.toISOString(),
      isMe: true
    };

    const systemMessage = {
      id: messages.length + 2,
      user: 'ÏãúÏä§ÌÖú',
      text: `"${currentRoom.name}"Ïùò ÏÉÅÌÉúÍ∞Ä "ÏòàÏïΩÏ§ë"ÏúºÎ°ú ÏûêÎèô Î≥ÄÍ≤ΩÎêòÏóàÏäµÎãàÎã§.`,
      time: now.toISOString(),
      isMe: false
    };

    const updatedRooms = rooms.map(room =>
      room.id === currentRoomId
