import React, { useState } from 'react';
import '../../css/login.css';

function Signup() {
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPw, setConfirmPw] = useState('');
  const [agreement, setAgreement] = useState(false);
  const [error, setError] = useState('');
  const [isIdChecked, setIsIdChecked] = useState(false);
  const [pwMismatch, setPwMismatch] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!name || !phone || !userId || !password || !confirmPw) {
      setError('Î™®Îì† Ìï≠Î™©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    if (!isIdChecked) {
      setError('ÏïÑÏù¥Îîî Ï§ëÎ≥µ ÌôïÏù∏ÏùÑ Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    if (password !== confirmPw) {
      setError('ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏùºÏπòÌïòÏßÄ ÏïäÏäµÎãàÎã§.');
      return;
    }

    if (!agreement) {
      setError('ÏïΩÍ¥ÄÏóê ÎèôÏùòÌï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    setError('');
    alert(`ÌöåÏõêÍ∞ÄÏûÖ ÏôÑÎ£å! üéâ\nÏù¥Î¶Ñ: ${name}, ÏïÑÏù¥Îîî: ${userId}`);
  };

  const handleCheckId = () => {
    if (!userId.trim()) {
      alert('ÏïÑÏù¥ÎîîÎ•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }
    alert(`ÏïÑÏù¥Îîî "${userId}"Îäî ÏÇ¨Ïö© Í∞ÄÎä•Ìï©ÎãàÎã§.`);
    setIsIdChecked(true);
  };

  const handlePhoneVerify = () => {
    if (!phone.trim()) {
      alert('Ìú¥ÎåÄÌè∞ Î≤àÌò∏Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }
    alert('Ïù∏Ï¶ùÎ≤àÌò∏Í∞Ä Î∞úÏÜ°ÎêòÏóàÏäµÎãàÎã§! (Ïã§Ï†ú ÎèôÏûëÏùÄ Íµ¨ÌòÑ ÌïÑÏöî)');
  };

  const handleConfirmPw = (value) => {
    setConfirmPw(value);
    setPwMismatch(password && value && password !== value);
  };

  return (
    <div className="login-container">
      <h1 className="spotlight-logo">
        <span className="black">SPOT</span>
        <span className="light animated">LIGHT</span>
      </h1>

      <form className="signup-box" onSubmit={handleSubmit}>
        <h2>Í∞ÄÏûÖÏùÑ ÏãúÏûëÌï©ÎãàÎã§!</h2>

        <div className="form-section">
          <h3 className="form-title">ÌöåÏõê Ï†ïÎ≥¥</h3>
          <div className="input-wrap">
            <input
              type="text"
              placeholder="Ïù¥Î¶Ñ"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="input-wrap phone-check">
            <input
              type="tel"
              placeholder="Ìú¥ÎåÄÌè∞ Î≤àÌò∏"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
            />
            <button type="button" className="check-btn" onClick={handlePhoneVerify}>
              Ïù∏Ï¶ù ÏöîÏ≤≠
            </button>
          </div>
        </div>

        <div className="form-section">
          <h3 className="form-title">Í≥ÑÏ†ï</h3>
          <div className="input-wrap id-check">
            <input
              type="text"
              placeholder="ÏïÑÏù¥Îîî ÏûÖÎ†•"
              value={userId}
              onChange={(e) => {
                setUserId(e.target.value);
                setIsIdChecked(false);
              }}
            />
            <button type="button" className="check-btn" onClick={handleCheckId}>
              Ï§ëÎ≥µ ÌôïÏù∏
            </button>
          </div>
          <div className="input-wrap">
            <input
              type="password"
              placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏ ÏûÖÎ†•"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div className="input-wrap">
            <input
              type="password"
              placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏"
              value={confirmPw}
              onChange={(e) => handleConfirmPw(e.target.value)}
            />
          </div>
          {pwMismatch && (
            <div className="error-message">ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏùºÏπòÌïòÏßÄ ÏïäÏäµÎãàÎã§.</div>
          )}
        </div>

        <div className="terms">
          <input
            type="checkbox"
            id="agree"
            checked={agreement}
            onChange={(e) => setAgreement(e.target.checked)}
          />
          <label htmlFor="agree">Ïù¥Ïö© ÏïΩÍ¥ÄÏóê ÎèôÏùòÌï©ÎãàÎã§.</label>
        </div>

        {error && <div className="error-message">{error}</div>}

        <button type="submit" className="btn login-btn shine-button">
          ÌöåÏõêÍ∞ÄÏûÖ ÏôÑÎ£å
        </button>
      </form>
    </div>
  );
}

export default Signup;
