import React, { useState } from 'react';
import '../../css/myPage/myPage_category.css';

function MyPageCategory() {
  const [showDropdown, setShowDropdown] = useState(false);

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  return (
    <div className="mypage-category">
      <button className="category-button shine-button" onClick={toggleDropdown}>
        ☰ <span className="yellow-text">카테고리</span>
      </button>

      {showDropdown && (
        <div className="dropdown-menu">
          <div className="dropdown-item">한복</div>
          <div className="dropdown-item">정장</div>
          <div className="dropdown-item">드레스</div>
        </div>
      )}

      <div className="category-menu">
        <span>커뮤니티</span>
        <span>대여 절차 안내</span>
        <span>사이즈 가이드</span>
        <span>후기 모아보기</span>
      </div>
    </div>
  );
}

export default MyPageCategory;
