import React, { useState } from 'react';
import '../css/login.css';

function Signup() {
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleNext = () => {
    if (!name || !phone || !username || !password) {
      alert('모든 항목을 입력해주세요.');
      return;
    }
    alert('다음 단계로 이동합니다!');
  };

  return (
    <div className="login-container">
      <h1 className="spotlight-logo">
        <span className="black">SPOT</span>
        <span className="light animated">LIGHT</span>
      </h1>

      <div className="signup-box styled-form">
        <div className="form-group">
          <label htmlFor="name">이름</label>
          <input
            id="name"
            type="text"
            placeholder="이름을 입력하세요"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>

        <div className="form-group">
          <label htmlFor="phone">휴대폰 번호</label>
          <input
            id="phone"
            type="tel"
            placeholder="-없이 숫자입력"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
          />
        </div>

        <div className="form-group">
          <label htmlFor="username">계정</label>
          <input
            id="username"
            type="text"
            placeholder="아이디 입력"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="비밀번호 입력"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>

        <button className="btn login-btn full-width" onClick={handleNext}>
          다음
        </button>
      </div>
    </div>
  );
}

export default Signup;
