import React, { useState } from 'react';
import '../../css/myPage/myPage_category.css';
import MyPageDropdown from './myPage_dropdown';

function MyPageCategory() {
  const [showDropdown, setShowDropdown] = useState(false);

  const toggleDropdown = () => {
    setShowDropdown((prev) => !prev);
  };

  return (
    <div className="mypage-category">
      <button className="category-button shine-button" onClick={toggleDropdown}>
        ☰ <span className="highlight-yellow">카테고리</span>
      </button>
      <div className="category-menu">
        <span>커뮤니티</span>
        <span>대여 절차 안내</span>
        <span>사이즈 가이드</span>
        <span>후기 모아보기</span>
      </div>
      {showDropdown && <MyPageDropdown />}
    </div>
  );
}

export default MyPageCategory;
